mapper.select:
select travelID, cu_request_id, customer, departure, destination, start_date, return_date, state from TravelDetails 


        travelID = #{travelID}


        and cu_request_id = #{cu_request_id}


        and customer = #{customer}


        and departure = #{departure}


        and destination = #{destination}


        and start_date = #{start_date}


        and return_date = #{return_date}


        and state = #{state}

select airlineID, name, country, address, state from Airline 


        airlineID = #{airlineID}


        and name = #{name}


        and country = #{country}


        and address = #{address}


        and state = #{state}

select requestID, travelID, airlineID, departure, destination, start_date, return_date, inboundFlightNumber, outboundFlightNumber, price, state from AirlineRequest 


        requestID = #{requestID}


        and travelID = #{travelID}


        and airlineID = #{airlineID}


        and departure = #{departure}


        and destination = #{destination}


        and start_date = #{start_date}


        and return_date = #{return_date}


        and inboundFlightNumber = #{inboundFlightNumber}


        and outboundFlightNumber = #{outboundFlightNumber}


        and price = #{price}


        and state = #{state}

select offerID, requestID, airlineID, inboundFlightNumber, outboundFlightNumber, price, state from Offer 


        offerID = #{offerID}


        and requestID = #{requestID}


        and airlineID = #{airlineID}


        and inboundFlightNumber = #{inboundFlightNumber}


        and outboundFlightNumber = #{outboundFlightNumber}


        and price = #{price}


        and state = #{state}
