mapper.select:
select travelID, state, departure, destination, start_date, return_date, hotel, price from TravelDetails 


        travelID = #{travelID}


        and state = #{state}


        and departure = #{departure}


        and destination = #{destination}


        start_date = #{start_date}


        and return_date = #{return_date}


        and hotel = #{hotel}


        and price = #{price}

select flightID, travelID, airline, inboundFlightNumber, outboundFlightNumber, price, state from Flights 


        flightID = #{flightID}


        and travelID = #{travelID}


        and airline = #{airline}


        and inboundFlightNumber = #{inboundFlightNumber}


        and outboundFlightNumber = #{outboundFlightNumber}


        and price = #{price}


        and state = #{state}

select requestID, messageRequestID, departure, destination, start_date, return_date, state from Request 


        requestID = #{requestID}


        and messageRequestID = #{messageRequestID}


        and departure = #{departure}


        and destination = #{destination}


        and start_date = #{start_date}


        and return_date = #{return_date}


        and state = #{state}

select offerID, requestID, inboundFlightNumber, outboundFlightNumber, price, state from Offer 


        offerID = #{offerID}


        and requestID = #{requestID}


        and inboundFlightNumber = #{inboundFlightNumber}


        and outboundFlightNumber = #{outboundFlightNumber}


        and price = #{price}


        and state = #{state}
